carouselBasicCode:
import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'nz-demo-carousel-basic',
    template: `
        <nz-carousel [nzEffect]="effect">
            <div nz-carousel-content *ngFor="let index of array">
                <h3 class="text-white">{{index}}</h3>
            </div>
        </nz-carousel>`,
    styles  : [
        `[nz-carousel-content] {
            text-align: center;
            height: 160px;
            line-height: 160px;
            background: #364d79;
            color: #fff;
            overflow: hidden;
        }

        [nz-carousel-content] h3 {
            height: 160px;
            line-height: 160px;
        }    
    `]
})
export class NzDemoCarouselBasicComponent implements OnInit {
    array = [ 1, 2, 3, 4 ];
    effect = 'scrollx';

    ngOnInit() {
        setTimeout(() => {
            this.effect = 'fade';
        }, 3000);
    }
}

