transferBasicCode:
import { Component, OnInit } from '@angular/core';
import { NzMessageService } from 'ng-zorro-antd';

@Component({
    selector: 'nz-demo-transfer-basic',
    template: `
        <nz-transfer
            [nzDataSource]="list"
            [nzTitles]="['Source', 'Target']"
            (nzSelectChange)="select($event)"
            (nzChange)="change($event)">
        </nz-transfer>
    `
})
export class NzDemoTransferBasicComponent implements OnInit {
    // tslint:disable-next-line:no-any
    list: any[] = [];

    ngOnInit(): void {
        for (let i = 0; i < 20; i++) {
            this.list.push({
                key     : i.toString(),
                title   : `content${i + 1}`,
                disabled: i % 3 < 1,
            });
        }

        [ 2, 3 ].forEach(idx => this.list[ idx ].direction = 'right');
    }

    select(ret: {}): void {
        console.log('nzSelectChange', ret);
    }

    change(ret: {}): void {
        console.log('nzChange', ret);
    }
}

transferSearchCode:
import { Component, OnInit } from '@angular/core';

@Component({
    selector: 'nz-demo-transfer-search',
    template: `
        <nz-transfer
            [nzDataSource]="list"
            nzShowSearch
            [nzFilterOption]="filterOption"
            (nzSearchChange)="search($event)"
            (nzSelectChange)="select($event)"
            (nzChange)="change($event)">
        </nz-transfer>
    `
})
export class NzDemoTransferSearchComponent implements OnInit {
    // tslint:disable-next-line:no-any
    list: any[] = [];

    ngOnInit(): void {
        for (let i = 0; i < 20; i++) {
            this.list.push({
                key        : i.toString(),
                title      : `content${i + 1}`,
                description: `description of content${i + 1}`,
                direction  : Math.random() * 2 > 1 ? 'right' : ''
            });
        }
    }

    // tslint:disable-next-line:no-any
    filterOption(inputValue: string, item: any): boolean {
        return item.description.indexOf(inputValue) > -1;
    }

    search(ret: {}): void {
        console.log('nzSearchChange', ret);
    }

    select(ret: {}): void {
        console.log('nzSelectChange', ret);
    }

    change(ret: {}): void {
        console.log('nzChange', ret);
    }
}

transferAdvancedCode:
import { Component, OnInit } from '@angular/core';
import { NzMessageService } from 'ng-zorro-antd';

@Component({
    selector: 'nz-demo-transfer-advanced',
    template: `
        <nz-transfer
            [nzDataSource]="list"
            nzShowSearch
            [nzOperations]="['to right', 'to left']"
            [nzListStyle]="{'width.px': 300, 'height.px': 400}"
            [nzRender]="render"
            [nzFooter]="footer"
            (nzSelectChange)="select($event)"
            (nzChange)="change($event)">
            <ng-template #render let-item>
                {{ item.title }}-{{ item.description }}
            </ng-template>
            <ng-template #footer let-direction>
                <button nz-button (click)="reload(direction)" [nzSize]="'small'" class="float-right m-10">reload</button>
            </ng-template>
        </nz-transfer>
    `
})
export class NzDemoTransferAdvancedComponent implements OnInit {
    list = [];

    ngOnInit(): void {
        this.getData();
    }

    getData(): void {
        const ret = [];
        for (let i = 0; i < 20; i++) {
            ret.push({
                key        : i.toString(),
                title      : `content ${i + 1}`,
                description: `description of content${i + 1}`,
                direction  : Math.random() * 2 > 1 ? 'right' : ''
            });
        }
        this.list = ret;
    }

    reload(direction: string): void {
        this.getData();
        this.msg.success(`your clicked ${direction}!`);
    }

    select(ret: {}): void {
        console.log('nzSelectChange', ret);
    }

    change(ret: {}): void {
        console.log('nzChange', ret);
    }

    constructor(public msg: NzMessageService) {
    }
}

