import { Component, ViewEncapsulation } from '@angular/core';

@Component({
    selector     : 'nz-demo-badge-change',
    encapsulation: ViewEncapsulation.None,
    template     : `
        <div class="m-b-10">
            <nz-badge class="m-r-35" [nzCount]="count">
                <a class="head-example"></a>
            </nz-badge>
            <nz-button-group>
                <button nz-button (click)="minCount()"><i nz-icon type="minus"></i></button>
                <button nz-button (click)="addCount()"><i nz-icon type="plus"></i></button>
            </nz-button-group>
        </div>
        <div>
            <nz-badge class="m-r-35" [nzDot]="dot">
                <a class="head-example"></a>
            </nz-badge>
            <nz-switch [(ngModel)]="dot"></nz-switch>
        </div>
    `
})
export class NzDemoBadgeChangeComponent {

    count = 5;
    dot = true;

    addCount(): void {
        this.count++;
    }

    minCount(): void {
        this.count--;
        if (this.count < 0) {
            this.count = 0;
        }
    }
}
